import React, { useState, useEffect } from "react";
import './Header.css';

const Header = ({ products, setFilteredProducts }) => {
  const [selectedCategory, setSelectedCategory] = useState("all");
  const categories = ["all", "women's clothing", "electronics", "jewelery", "men's clothing"]; // Agrega las categorías

  useEffect(() => {
    if (selectedCategory === "all") {
      setFilteredProducts(products);
    } else {
      const filtered = products.filter(
        (product) => product.category === selectedCategory
      );
      setFilteredProducts(filtered);
    }
  }, [selectedCategory, products, setFilteredProducts]);

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
  };

  return (
    <div className="header">
      <div className="user-circle">{selectedCategory}</div>
      <div className="category-filter">
        <label htmlFor="category">Filtrar por categoría:</label>
        <select id="category" value={selectedCategory} onChange={handleCategoryChange}>
          {categories.map((category) => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default Header;
