import React, { useEffect, useState } from 'react';
import Navbar from './components/navbar';
import SearchBox from './components/searchBox';
import Home from './pages/home';
import { FetchMealByFirstLetter } from './services/mealtService.js';

function App() {
	const [meals, setMeals] = useState([]);
	const [searchValue, setSearchValue] = useState("");

	useEffect(() => {
		async function fetchMeals() {
			try {
				const data = await FetchMealByFirstLetter();
				setMeals(data);
			} catch (error) {
				console.error('Error al obtener comidas:', error);
			}
		}

		fetchMeals();
	}, []);

	const handleSearchChange = (value) => {
		setSearchValue(value);
	};

	const filteredMeals = meals.filter((meal) => {
		if (!searchValue) {
			return true;
		}

		const mealName = meal.strMeal.toLowerCase();
		const searchValueLowerCase = searchValue.toLowerCase();
		return mealName.includes(searchValueLowerCase);
	});

	return (
		<div className='App'>
			<Navbar>
				<SearchBox onSearchChange={handleSearchChange} />
			</Navbar>
			<Home meals={filteredMeals} />
		</div>
	);
}

export default App;
