import React, { useState, useEffect } from "react";
import Stock from "./stock";
import Product from "./product";
import { Routes, Route } from "react-router-dom";
import Header from "./Header";

function App() {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const categories = ["all", "women's clothing", "electronics", "jewelery", "men's clothing"]; // Agrega las categorías

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data); // Inicialmente, mostrar todos los productos
      })
      .catch((error) => console.error("Error al obtener los productos", error));
  }, []);

  return (
    <div className="App">
      <Header
        products={products}
        setFilteredProducts={setFilteredProducts}
        categories={categories} // Pasa las categorías al componente Header
      />
      <Routes>
        <Route path="/" element={<Stock products={filteredProducts} />} />
        <Route path="/products/:id" element={<Product />} />
      </Routes>
    </div>
  );
}

export default App;
