import React, { useState, useEffect } from "react";
import './Header.css';

const Header = ({ products, setFilteredProducts }) => {
  const [selectedCategory, setSelectedCategory] = useState("all"); // Inicialmente, mostrar todos los productos

  useEffect(() => {
    if (selectedCategory === "all") {
      // Mostrar todos los productos si la categoría seleccionada es "all"
      setFilteredProducts(products);
    } else {
      // Filtrar los productos por categoría
      const filtered = products.filter(product => product.category === selectedCategory);
      setFilteredProducts(filtered);
    }
  }, [selectedCategory, products, setFilteredProducts]);

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
  };

  return (
    <div className="header">
      <div className="user-circle">
        {selectedCategory}
      </div>
      <div className="category-filter">
        <label htmlFor="category">Filtrar por categoría:</label>
        <select id="category" value={selectedCategory} onChange={handleCategoryChange}>
          <option value="all">Todos</option>
          <option value="electronics">Electrónicos</option>
          <option value="clothing">Ropa</option>
          <option value="jewelery">Joyería</option>
          {/* Agrega más opciones de categoría según tus datos */}
        </select>
      </div>
    </div>
  );
}

export default Header;
