import React, { useEffect, useState } from 'react';
import './index.css';
import { FetchMealByFirstLetter } from './tu-ruta-hacia-los-servicios'; // Reemplaza 'tu-ruta-hacia-los-servicios' con la ruta real

const MealPreviwGrid = () => {
  const [meals, setMeals] = useState([]);

  useEffect(() => {
    async function fetchMeals() {
      try {
        const letter = 'b'; // Cambia 'b' por la letra que desees buscar
        const data = await FetchMealByFirstLetter(letter);
        setMeals(data);
      } catch (error) {
        console.error('Error al obtener comidas:', error);
      }
    }

    fetchMeals();
  }, []);

  return (
    <div className='meal-grid'>
      {meals.map((meal) => (
        <MealPreviw key={meal.idMeal} meal={meal} />
      ))}
    </div>
  );
};

export default MealPreviwGrid;
