import React, { useState, useEffect } from "react";

import './index.css';
import MealPreviw from '../mealPreview';

const MealPreviwGrid = ({ meals, filt }) => {

	const [startIndex, setStartIndex] = useState(0);
  	const [showAll, setShowAll] = useState(false); 

	useEffect(() => {
		setStartIndex(0); 
	  }, [selectedCategory]);

	const filteredProducts = filt === ""
	  ? meals
	  : meals.filter((product) => product.category === filt);
	
	const productsToDisplay = showAll ? filteredProducts : filteredProducts.slice(startIndex, startIndex + 4);


	return (
		<div className='meal-grid'>
		{productsToDisplay.map((meal, index) => (
		 	<MealPreviw meal = {meal}></MealPreviw>
		))}
		</div>
	);
};

export default MealPreviwGrid;
